#include <iostream>
using namespace std;

int main() {
    int n;
    long factorial = 1.0;

    cin >> n;

    if (n < 0)
        cout << "Error! Factorial of a negative number doesn't exist.";
    else {
        for(int i = 1; i <= n; ++i) {
            factorial *= i;
        }
        cout << "Factorial of " << n << " = " << factorial;
    }

    return 0;
}

#include <iostream>
using namespace std;

// function prototype
void reverse(const string& a);

int main() {
  string str;

  getline(cin, str);

  // function call
  reverse(str);

  return 0;    
}

// function definition
void reverse(const string& str) {
  // store the size of the string
  size_t numOfChars = str.size();

  if(numOfChars == 1) {
    cout << str << endl;
  }
  else {
    cout << str[numOfChars - 1];

    // function recursion
    reverse(str.substr(0, numOfChars - 1));
  }
}
